
$(document).ready(function(){
	$(".readmore a").click(showHidenP);
});
	function showHidenP() {
	$(".readmore").hide();
	$("#post").slideDown();
	$("#show-this-on-click").show();
	$(".readless").show(); 
 };

 $(document).ready(function(){
	$(".readless a").click(hidP);
}); 

	function hidP() {
	$("#show-this-on-click").hide();
	$(".readless").hide(); 
	$(".readmore").show();
	$("#post").slideup();
 };

$(document).ready(function(){
	$(".learnmore").click(spanShow);
}); 
	function spanShow(){
	$(".learnmore").hide();
	$("#sidebar p").slideDown();
	$("#learnmoretext").show();
	
};

